incStratUnc <- function(uSR=NULL,pP,lwdLin=2)	{	if (is.null(uSR))		{		styles <- list(certain=c(lwdLin,"black",1), possible=c(lwdLin,"grey80",1), extension=c(lwdLin,"black",2), CI=c(lwdLin*0.8,"black",1))		dts <- vector("list",length(pP$nm))  ;  for (i in 1:length(pP$st)) {dts[[i]] <- c(pP$st[i],pP$en[i])}		usr <- list(id=as.list(pP$nm),dates=dts, types=as.list(rep(1,length(pP$nm))), styles=styles)		}	if (!is.null(uSR))		{		reqTypes <- unique(unlist(uSR$types))		if(sum(sapply(1:length(reqTypes), function(i) {is.null(uSR$styles[[reqTypes[i]]])}))!=0)				stop("Not all types have a defined style")		badd <- which(sapply(1:length(uSR$dates), function(i) {length(uSR$dates[[i]])!=(length(uSR$types[[i]])+1)}))		if (length(badd)>0) {cat(paste("Mismatch at lineage ", badd, sep=""))}		cat("\n") 		if(sum(sapply(1:length(uSR$dates), function(i) {length(uSR$dates[[i]])!=(length(uSR$types[[i]])+1)}))!=0)				stop("The number of dates and types do not match. N(dates)!=(N(types)+1) for all lineages.")		usr <- uSR		}	return(usr)	}